scripts.draw:
function draw(){
    var config = window.chartWerk;
	d3.select("#chart")
	    .append("svg")
	    .attr({
	        "width": (600 * (1 - config.margins.double.bottom)),
	        "height": 300
	    })
	    .style({
	        "background": "pink"
	    });
}

scripts.styles:
@import url(https://fonts.googleapis.com/css?family=Montserrat:400,700);

#chartWerk {
    font-family:'Montserrat', arial, sans-serif; 
}
#chartWerk #headline { 
    font: bold 28px 'Montserrat', arial;
}
#chartWerk #chatter {
    font: normal 14px/17px 'Montserrat', arial;
    margin-top:5px;
}
#chartWerk #footnote, #chartWerk #source {
    font: normal 12px/15px arial, sans-serif;
    color:grey;
}
#chartWerk #credit {
    font: italic 14px/17px arial, sans-serif;
    color:grey;
}
